	// ending 다시시작 or 종료
	public void restart() {

		Scanner sc = new Scanner(System.in);
		System.out.println("[1]다시 시작? [2]종료");
		int reNum = sc.nextInt();

		int i = 1;
		if (i == reNum) {
			System.out.println("다시 사과 구하러 가주아~~");
		} else if (i != reNum) {
			System.out.println("안녕~~");
		}

	}

